(function(){tinymce.PluginManager.requireLangPack('uibkextras');tinymce.create('tinymce.plugins.UibkextrasPlugin',{init:function(ed,url){var uibkextrasRessourceUrl=tinyMCE.get('body').getParam('uibkextras_ressourceUrl');ed.addCommand('mcePasteNewIcon',function(){var html='<span title="new" class="sprite icon_new" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteArrowBlackIcon',function(){var html='<span title="black arrow" class="sprite icon_arrowblack" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteArrowOrangeIcon',function(){var html='<span title="orange arrow" class="sprite icon_arroworange" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteImageIcon',function(){var html='<span title="Image" class="sprite icon_image" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteDocumentIcon',function(){var html='<span title="Document" class="sprite icon_document" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteMsWordIcon',function(){var html='<span title="Word" class="sprite icon_word" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteMsExcelIcon',function(){var html='<span title="Excel" class="sprite icon_excel" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteMsPPTIcon',function(){var html='<span title="Powerpoint" class="sprite icon_ppt" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePastePDFIcon',function(){var html='<span title="PDF" class="sprite icon_pdf" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteEmailIcon',function(){var html='<span title="Email" class="sprite icon_email" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteIntranetIcon',function(){var html='<span title="Intranet" class="sprite icon_intranet" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});ed.addCommand('mcePasteExternalLinkIcon',function(){var html='<span title="External Link" class="sprite icon_externallink" ></span>';tinyMCE.get('body').execCommand('mceInsertContent',false,html);});var uibkextrasIconNames=tinyMCE.get('body').getParam('uibkextras_iconNames').split(',');for(var i=0;i<uibkextrasIconNames.length;i++){var skey_value=uibkextrasIconNames[i].split('=');ed.addButton('uibkextras_'+skey_value[0]+'',{title:'uibkextras'+skey_value[0]+'.desc',cmd:'mcePaste'+skey_value[0]+'Icon'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('uibkextras_'+skey_value[0],n.nodeName=='IMG');});}},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'UIBK image-buttons-extras TinyMCE plugin',author:'Severin.Gehwolf@uibk.ac.at',authorurl:'http://www.uibk.ac.at/',infourl:'http://www.uibk.ac.at/zid/systeme/xims/tinymce_uibkextras.html',version:"0.02"};}});tinymce.PluginManager.add('uibkextras',tinymce.plugins.UibkextrasPlugin);})();